export class Transformer {
  buildInlineAdf(url) {
    return {
      type: 'inlineCard',
      attrs: {
        url
      }
    };
  }
  buildBlockAdf(url) {
    return {
      type: 'blockCard',
      attrs: {
        url
      }
    };
  }
  buildEmbedAdf(url) {
    return {
      type: 'embedCard',
      attrs: {
        url,
        layout: 'wide'
      }
    };
  }
  toAdf(url, appearance) {
    switch (appearance) {
      case 'inline':
        return this.buildInlineAdf(url);
      case 'block':
        return this.buildBlockAdf(url);
      case 'embed':
        return this.buildEmbedAdf(url);
    }
  }
}